USE Dina200485862;

CREATE TABLE IF NOT EXISTS COVID (
    `id` INT,
    `Date` date,
    `covid19_cases_unvac` INT,
    `covid19_cases_partial_vac` INT,
    `covid19_cases_full_vac` INT
);
    
    Select Date, covid19_cases_unvac,covid19_cases_partial_vac,covid19_cases_full_vac, 
    (covid19_cases_unvac + covid19_cases_partial_vac+ covid19_cases_full_vac)
    as 'totalCases' from COVID
    order by Date;
    
